import { type SVGProps } from "react";

// Icon component - SVG wrapper for icons
// Includes Simple Icons (tech/brand) and custom UI icons

export interface IconProps extends SVGProps<SVGSVGElement> {
    size?: number | string;
}

// Base icon wrapper
function createIcon(path: string, title: string, viewBox = "0 0 24 24") {
    return function Icon({ size = 24, className = "", ...props }: IconProps) {
        return (
            <svg
                role="img"
                viewBox={viewBox}
                width={size}
                height={size}
                fill="currentColor"
                className={className}
                aria-label={title}
                {...props}
            >
                <title>{title}</title>
                <path d={path} />
            </svg>
        );
    };
}

// Stroke-based icon wrapper (for UI icons)
function createStrokeIcon(paths: string[], title: string) {
    return function Icon({ size = 24, className = "", ...props }: IconProps) {
        return (
            <svg
                role="img"
                viewBox="0 0 24 24"
                width={size}
                height={size}
                fill="none"
                stroke="currentColor"
                strokeWidth="2"
                strokeLinecap="round"
                strokeLinejoin="round"
                className={className}
                aria-label={title}
                {...props}
            >
                <title>{title}</title>
                {paths.map((d, i) => (
                    <path key={i} d={d} />
                ))}
            </svg>
        );
    };
}

